            }
        }
        int ct = cnt[1];
        LL cof = f[ct];
        for (int i = 0; i &lt;= ct &amp;&amp; i &lt; 2048; i++) {
            dp[0][i &gt;&gt; 1] += g[ct - i] * g[i] % MOD;
            if (dp[0][i &gt;&gt; 1] &gt; MOD) dp[0][i &gt;&gt; 1] -= MOD;
        }

        for (int i = 1, m = 1024; i &lt;= 11; i++, m &gt;&gt;= 1) {
            ct = cnt[1 &lt;&lt; i];
            cof = cof * f[ct] % MOD;
            for (int j = 0; j &lt; m; j++) { 
                if (dp[i - 1][j]) {
