#pragma comment(linker, "/STACK:1024000000,1024000000")//C++加栈
#include
#include
#include
#include
#include
#include
#include
#include<map>
#include
#include
#include
using namespace std;
#if defined (_WIN32) || defined (__WIN32) || defined (WIN32) || defined (__WIN32__)
#define LL __int64
#define LLS "%" "I" "6" "4" "d"
#define LLU "%" "I" "6" "4" "u"
#define LL_MAX _I64_MAX

#else
#define LL long long
#define LLS "%" "l" "l" "d"
#define LLU "%" "l" "l" "u"
#define LL_MAX _I64_MAX
#endif
#define N 200005
#define M 1000005
int n, m;
const int inf = ~0u &gt;&gt; 1;
const LL lnf = ~0ull &gt;&gt; 1;
struct edge {
	int v, nxt;
} e[M &lt;&lt; 1];
struct bridge {
	int u, v;
	bridge(int u, int v) {
		this-&gt;u = u;
		this-&gt;v = v;
	}
};
int pos, head[N];
int top, stk[N];
int pre[N], dfs_clock, belong[N], bcnt;
vector brg;
void InitEdge() {
	memset(head, -1, sizeof(head));
	pos = 0;
}
void add(int u, int v) {
	e[pos].v = v;
	e[pos].nxt = head[u];
	head[u] = pos++;
}
int dfs(int u, int fa) {
	int lowu = pre[u] = ++dfs_clock;
	stk[top++] = u;
	bool vis = true;
	for (int i = head[u]; ~i; i = e[i].nxt) {
		int v = e[i].v;
		if (v == fa &amp;&amp; vis) {
			vis = false; //u-v的反向边一定是v-u邻接表里的第一条边。
			continue;
		}
		if (!pre[v]) {
			int lowv = dfs(v, u);
			lowu = min(lowu, lowv);
			if (lowv &gt; pre[u]) { //u-v为桥
				brg.push_back(bridge(u, v));
			}
		} else lowu = min(lowu, pre[v]);
	}
	if (pre[u] == lowu) {
		bcnt++;
		do {
			belong[stk[--top]] = bcnt;
		} while (stk[top] != u);
	}
	return lowu;
}
void tarjan() {
	top = bcnt = dfs_clock = 0;
	memset(pre, 0, sizeof(pre));
	memset(belong, 0, sizeof(belong));
	brg.clear();
	for (int i = 1; i &lt;= n; i++) {
		if (!pre[i]) dfs(i, -1);
	}
}
void rebuild() { //根据桥来缩点重构图
	InitEdge();
	for (int i = 0; i &lt; (int) brg.size(); i++) {
		int u = belong[brg[i].u];
		int v = belong[brg[i].v];
		add(u, v);
		add(v, u);
	}
}
int diameter = 0;
int findR(int u, int fa) {
	int h1 = 0, h2 = 0; // 以u为根，h1最高的儿子，h2次高儿子。放在dfs里面两者不会重合。
	for (int i = head[u]; ~i; i = e[i].nxt) {
		int v = e[i].v;
		if (v == fa) continue;
		int h = findR(v, u) + 1; //若路径有权重，则把1改为u-v的权重
		if (h &gt; h1) h2 = h1, h1 = h;
		else h2 = max(h, h2);
	}
	diameter = max(diameter, h1 + h2);
	return h1;
}
void tree_diameter() {
	diameter = 0;
	findR(1, -1);
}
int main() {
	while (scanf("%d%d", &amp;n, &amp;m), n || m) {
		int u, v;
		InitEdge();
		for (int i = 0; i &lt; m; i++) {
			scanf("%d%d", &amp;u, &amp;v);
			add(u, v);
			add(v, u);
		}
		tarjan();
		rebuild();
		tree_diameter();
		printf("%d\n", bcnt - diameter - 1);
	}
}
