#if defined (_WIN32) || defined (__WIN32) || defined (WIN32) || defined (__WIN32__)
#define LL __int64
#define LLS "%" "I" "6" "4" "d"
#define LLU "%" "I" "6" "4" "u"
#define LL_MAX _I64_MAX

#else
#define LL long long
#define LLS "%" "l" "l" "d"
#define LLU "%" "l" "l" "u"
#define LL_MAX _I64_MAX
#endif
#define N 10005
#define M 100005
int n, m;
const int inf = ~0u &gt;&gt; 1;
const LL lnf = ~0ull &gt;&gt; 1;
struct edge {
	int v, w, nxt;
	bool mark;
} e[M &lt;&lt; 1];
struct bridge {
	int u, v, w;
	bridge(int u, int v, int w) {
		this-&gt;u = u;
		this-&gt;w = w;
		this-&gt;v = v;
	}
};
int pos, head[N];
int top, stk[N];
int pre[N], dfs_clock, belong[N], bcnt;
vector brg;
void InitEdge() {
	memset(head, -1, sizeof(head));
	pos = 0;
}
void add(int u, int v, int w) {
	e[pos].v = v;
	e[pos].w = w;
	e[pos].nxt = head[u];
	head[u] = pos++;
}
int dfs(int u,int fa) {
	int lowu = pre[u] = ++dfs_clock;
	stk[top++] = u;
	bool vis=true;
	for (int i = head[u]; ~i; i = e[i].nxt) {
		int v = e[i].v;
		if(v==fa &amp;&amp; vis){
			vis=false;//重边
			continue;
		}
		if (!pre[v]) {
			int lowv = dfs(v,u);
			lowu = min(lowu, lowv);
			if (lowv &gt; pre[u]) { //u-v为桥
				brg.push_back(bridge(u, v, e[i].w));
			}
		} else lowu = min(lowu, pre[v]);
	}
	if (pre[u] == lowu) {
		bcnt++;
		do {
			belong[stk[--top]] = bcnt;
		} while (stk[top] != u);
	}
	return lowu;
}
void tarjan() {
	top = bcnt = dfs_clock = 0;
	memset(pre, 0, sizeof(pre));
	memset(belong, 0, sizeof(belong));
	brg.clear();
	for (int i = 1; i &lt;= n; i++) {
		if (!pre[i])dfs(i,-1);
	}
}
void rebuild() { //根据桥来缩点重构图
	InitEdge();
	for (int i = 0; i &lt; (int) brg.size(); i++) {
		int u = belong[brg[i].u];
		int v = belong[brg[i].v];
		int w = brg[i].w;
		add(u, v, w);
		add(v, u, w);
	}
}
int res;
int DP(int u, int fa) {
	int Min = inf;
	for (int i = head[u]; ~i; i = e[i].nxt) {
		int v = e[i].v;
		if (v == fa)continue;
		int w = DP(v, u);
		w = min(w, e[i].w);
		if (Min &gt; w) {
			res = min(res, Min);
			Min = w;
		} else res = min(res, w);
	}
	return Min;
}
int main() {
	while (~scanf("%d%d", &amp;n, &amp;m)) {
		int u, v, w;
		InitEdge();
		for (int i = 0; i &lt; m; i++) {
			scanf("%d%d%d", &amp;u, &amp;v, &amp;w);
			add(u, v, w);
			add(v, u, w);
		}
		tarjan();
		rebuild();
		bridge minbrg(-1, -1, inf);
		for (int i = 0; i &lt; (int) brg.size(); i++) {
			if (brg[i].w &lt; minbrg.w) {
				minbrg = brg[i];
			}
		}
		res = inf;
		DP(belong[minbrg.u], belong[minbrg.v]);
		DP(belong[minbrg.v], belong[minbrg.u]);
		if(res==inf)res=-1;
		printf("%d\n", res);
	}
}
