        Matrix A(n, k, 0), B(k, n, 0);
        for (int i = 0; i &lt; n; i++) {
            for (int j = 0; j &lt; k; j++) {
                scanf("%d", &amp;A.M[i][j]);
            }
        }
        for (int i = 0; i &lt; k; i++) {
            for (int j = 0; j &lt; n; j++) {
                scanf("%d", &amp;B.M[i][j]);
            }
        }
        Matrix mat = B * A;
        int r = n * n - 1;
        Matrix res = A * (mat ^ r) * B;
        int ans = 0;
        for (int i = 0; i &lt; n; i++) {
            for (int j = 0; j &lt; n; j++) {
                ans += res.M[i][j];
            }
        }
        printf("%d\n", ans);
    }
}
