		var scrollTarget = $('#' + $(this).children('.toc-text').first().text());
		$("html,body").animate({
			scrollTop: scrollTarget.prev().offset().top
		}, scrollSpeed);
	});
	C.click(function(e) {
		e.stopPropagation();
	});
	M.click(function() {
		C.toggle(hiddenSpeed, 'linear', function() {
			var T = $('#title-nav div');
			T.text() == '←' ? T.text('→') : T.text('←');
		});
	});
})(jQuery, 500, 200, 100);
</script>
