int dpM[20][N &lt;&lt; 1 | 1];
int lg2[N &lt;&lt; 1 | 1];
#define getLeft(R,L) (R-(L)+1)
void initRMQ(int n) { //dp[0][i]表示区间i的值。预先处理出来。
    lg2[0] = -1;
    int limit;
    for (int i = 1; i &lt;= n; i++) {
