int main(int argc, char **argv) {
    while(~scanf("%d",&amp;n)){
        for (int i = 1; i &lt;= n; ++i) {
            for (int j = 1; j &lt;= n; ++j) {
                scanf("%d",&amp;a[i][j]);
                f[i][j]=f[i-1][j]+f[i][j-1]-f[i-1][j-1]+a[i][j];
            }
        }
        int res=top=0;
        for (int x = 1; x &lt;= n; ++x) {
            for (int y = 1; y &lt;= n; ++y) {
                for (int i = 1; i &lt;= x; ++i) {
