```Java
import java.util.Scanner;
import java.math.*;
public class Main {
	static long fact[]=new long[15];
	static long kind;
	static int A[][]=new int[10][10];
	static final BigInteger MOD=BigInteger.valueOf(1&lt;&lt;30);
	static void getKind(int n){
		boolean mark[]=new boolean[10];
		for(int i,j,k,r=0;r&lt;2;r++){
			for (i = 0; i &lt; n; ++ i) mark[i] = false;
			for (i = 0; i &lt; n; ++ i) {
				if (mark[i]) continue;
				int cnt = 0;
				for (j = i; j &lt; n; ++ j) {
					for (k = 0; k &lt; n; ++ k) {
						if (r==1&amp;&amp;A[k][i] != A[k][j])break;
						if(r==0&amp;&amp;A[i][k] != A[j][k])break;
					}
					if (k == n) {
						++ cnt;
						mark[j] = true;
					}
				}
				kind /= fact[cnt];
			}
		}
	}
	public static void main(String[] args){
		Scanner cin=new Scanner(System.in);
		int n,T=cin.nextInt();
		fact[0]=1;
		for(int i=1;i&lt;=10;i++)fact[i]=fact[i-1]*i;
		while(T--&gt;0){
			n=cin.nextInt();
			for(int i=0;i&lt;n;i++){
				for(int j=0;j&lt;n;j++){
					A[i][j]=cin.nextInt();
				}
			}
			long res=0;
			Matrix matrix=new Matrix(n);
			kind=fact[n]*fact[n];
			getKind(n);
			if(kind%2==1){
				matrix.valueOf(A, 0);
				res=res ^(matrix.Det().mod(MOD).longValue());
			}
			matrix.valueOf(A, 1);
			res=res^(matrix.Det().mod(MOD).longValue());
			matrix.valueOf(A, -1);
			res=res^(matrix.Det().mod(MOD).longValue());
			System.out.println(res);
		}
		cin.close();
	}
}
class Matrix{
	BigInteger M[][]=new BigInteger[10][10];
	BigInteger ZERO,ONE;
	int n;
	Matrix(int n){
		this.n=n;
		ZERO=BigInteger.ZERO;
		ONE=BigInteger.ONE;
	}
	void valueOf(int A[][],int d){
		for(int i=0;i&lt;n;i++){
			for(int j=0;j&lt;n;j++){
				M[i][j]=BigInteger.valueOf(A[i][j]+d);
			}
		}
	}
	BigInteger Det(){
		BigInteger tmp, res = ONE, div = ONE;
		int i, j, k;
		for (i = 0; i &lt; n; ++ i) {
			for (j = i; j &lt; n; ++ j) {
				if (!M[j][i].equals(ZERO)) break;
			}
			if (j == n) return ZERO;
			if (j != i) {
				//res = res.negate();
				for (k = 0; k &lt; n; ++ k) {
				    tmp = M[j][k];
					M[j][k] = M[i][k];
					M[i][k] = tmp;
				}
			}
			res = res.multiply(M[i][i]);
